import isNil from 'lodash/isNil';
import React, { useEffect, useRef, memo, useCallback } from 'react';
import { View, Keyboard } from 'react-native';

import useState from '../../hooks/useStateUpdate';
import NumberInput from '../number-input';
import TextInput from '../text-input';
import type { TextInputInstance } from '../text-input/interface';

import DialogKeyboard from './dialog-keyboard';
import type { DialogInputProps, DialogAction, DialogInputState } from './interface';
import { getDefaultValue, callInterceptor } from '../../helpers';
import { usePersistFn } from '../../hooks';

/**
 * Dialog å¼¹å‡ºæ¡†
 * @description é…åˆå‡½æ•°çš„ä½¿ç”¨
 */
const DialogInput: React.FC<DialogInputProps> = ({
  showCancelButton = true,
  duration,
  beforeClose,
  onPressCancel,
  onPressConfirm,

  defaultValue,
  placeholder,
  type = 'text',
  autoFocus = true,
  textInput: { value: textInputValue, onChangeText, ...resetTextInputProps } = {},

  numberInput: { value: numberInputValue, onChange, ...resetNumberInputProps } = {},

  ...restProps
}) => {
  const isInputText = type === 'textarea' || type === 'text';
  const realValue = isInputText ? textInputValue : numberInputValue;

  const TextInputRef = useRef<TextInputInstance>(null);

  const [state, setState] = useState<DialogInputState>({
    visible: false,
    value: defaultValue || '',
    cancel: false,
    confirm: false,
    overlay: false,
  });

  duration = getDefaultValue(duration, 300);

  const onChangeTextPersistFn = usePersistFn((t: string) => {
    setState({
      value: t,
    });
    onChangeText?.(t);
  });

  const onChangePersistFn = usePersistFn((t: number) => {
    setState({
      value: t,
    });
    onChange?.(t);
  });

  const genOnPressBtn = (action: Exclude<DialogAction, 'overlay'>) => () => {
    Keyboard.dismiss();

    setState({
      [action]: true,
    });

    const actionCallback = action === 'confirm' ? onPressConfirm : onPressCancel;

    const finalValue = !isNil(state.value) ? `${state.value}` : '';

    callInterceptor(beforeClose, {
      args: [action, finalValue],
      done: () => {
        callInterceptor(actionCallback, {
          args: [finalValue],
          done: () => {
            setState({
              [action]: false,
              visible: false,
            });
          },
          canceled: () => {
            setState({
              [action]: false,
            });
          },
        });
      },
      canceled: () => {
        setState({
          [action]: false,
        });
      },
    });
  };

  useEffect(() => {
    setState({
      visible: true,
    });

    // å½“å¯¹è¯æ¡†å®Œå…¨æ˜¾ç¤ºçš„æ—¶å€™å†å»èšç„¦
    if (autoFocus) {
      setTimeout(() => {
        TextInputRef.current?.focus();
      }, duration);
    }
  }, [duration, autoFocus]);

  useEffect(() => {
    if (!isNil(realValue)) {
      setState({
        value: realValue,
      });
    }
  }, [realValue]);

  const onPressClose = useCallback(() => {
    setState({
      visible: false,
    });
  }, []);

  return (
    <DialogKeyboard
      {...restProps}
      showCancelButton={showCancelButton}
      closeOnPressOverlay={false}
      visible={state.visible}
      onPressConfirm={genOnPressBtn('confirm')}
      onPressCancel={genOnPressBtn('cancel')}
      cancelButtonLoading={state.cancel}
      confirmButtonLoading={state.confirm}
      onPressClose={onPressClose}
    >
      <View className='mx-4 mt-2 pb-1 overflow-hidden max-h-[200px]'>
        {type === 'textarea' || type === 'text' ? (
          <TextInput
            size='xl'
            {...resetTextInputProps}
            ref={TextInputRef}
            type={type}
            placeholder={placeholder}
            value={state.value as string}
            onChangeText={onChangeTextPersistFn}
            bordered
          />
        ) : (
          <NumberInput
            size='xl'
            {...resetNumberInputProps}
            ref={TextInputRef}
            type={type}
            placeholder={placeholder}
            value={state.value as number}
            onChange={onChangePersistFn}
            bordered
          />
        )}
      </View>
    </DialogKeyboard>
  );
};

export default memo(DialogInput);

import React from 'react'

import Portal from '../portal'

import Dialog from './dialog'
import DialogInput from './dialog-input'
import DialogKeyboard from './dialog-keyboard'
import DialogMethod from './dialog-method'
import type {
  DialogOptions,
  DialogAction,
  DialogInputOptions,
  DialogProps,
  DialogKeyboardProps,
} from './interface'

export const Instance = (opts: DialogOptions) => {
  return new Promise<DialogAction>(resolve => {
    const key = Portal.add(
      <DialogMethod
        {...opts}
        onClosed={() => {
          Portal.remove(key)

          opts.onClosed?.()
        }}
        onResponse={action => {
          resolve(action)
        }}
      />,
    )
  })
}

export const confirm = (options: Omit<DialogOptions, 'showCancelButton'>) =>
  Instance({
    showCancelButton: true,
    ...options,
  })

export const input = (opts: DialogInputOptions) => {
  const key = Portal.add(
    <DialogInput
      {...opts}
      onClosed={() => {
        Portal.remove(key)
        opts.onClosed?.()
      }}
    />,
  )
}

export const Component: React.FC<
  React.PropsWithChildren<DialogProps>
> = props => (
  <Portal>
    <Dialog {...props} />
  </Portal>
)

export const DialogComponent = Dialog

export const Keyboard: React.FC<
  React.PropsWithChildren<DialogKeyboardProps>
> = props => (
  <Portal>
    <DialogKeyboard {...props} />
  </Portal>
)

export const KeyboardComponent = DialogKeyboard

import React, { useState, useMemo, useEffect, memo } from 'react'
import type { ViewStyle } from 'react-native'
import { Keyboard } from 'react-native'
import { useSafeAreaInsets } from 'react-native-safe-area-context'

import Dialog from './dialog'
import type { DialogKeyboardProps } from './interface'

const DialogKeyboard: React.FC<DialogKeyboardProps> = ({
  style,
  safeAreaTop,
  ...restProps
}) => {
  const insets = useSafeAreaInsets()
  const [keyboardShow, setKeyboardShow] = useState(false)
  const dialogStyle = useMemo<ViewStyle>(
    () => ({
      position: 'absolute',
      top: safeAreaTop ?? insets.top,
    }),
    [insets.top, safeAreaTop],
  )

  useEffect(() => {
    const keyboardDidShow = Keyboard.addListener('keyboardDidShow', () => {
      setKeyboardShow(true)
    })
    const keyboardDidHide = Keyboard.addListener('keyboardDidHide', () => {
      setKeyboardShow(false)
    })

    return () => {
      keyboardDidShow.remove()
      keyboardDidHide.remove()
    }
  }, [])

  return (
    <Dialog
      {...restProps}
      style={
        keyboardShow ? (style ? [dialogStyle, style] : dialogStyle) : style
      }
    />
  )
}

export default memo(DialogKeyboard)

import React, { useEffect, memo } from 'react';

import { callInterceptor } from '../../helpers';
import useState from '../../hooks/useStateUpdate';

import Dialog from './dialog';
import type { DialogMethodProps, DialogAction, DialogMethodState } from './interface';

/**
 * Dialog å¼¹å‡ºæ¡†
 * @description é…åˆå‡½æ•°çš„ä½¿ç”¨
 */
const DialogMethod: React.FC<DialogMethodProps> = ({ beforeClose, onResponse, ...restProps }) => {
  const [state, setState] = useState<DialogMethodState>({
    visible: false,
    cancel: false,
    confirm: false,
    overlay: false,
  });

  const genOnPressBtn = (action: DialogAction) => () => {
    setState({
      [action]: true,
    });

    callInterceptor(beforeClose, {
      args: [action],
      done: () => {
        onResponse?.(action);
        setState({
          [action]: false,
          visible: false,
        });
      },
      canceled: () => {
        setState({
          [action]: false,
        });
      },
    });
  };

  useEffect(() => {
    setState({
      visible: true,
    });
  }, []);

  const onRequestClose = () => {
    genOnPressBtn('overlay')();
    return true;
  };

  const onPressClose = () => {
    setState({
      visible: false,
    });
  };

  return (
    <Dialog
      {...restProps}
      onRequestClose={onRequestClose}
      visible={state.visible}
      onPressConfirm={genOnPressBtn('confirm')}
      onPressCancel={genOnPressBtn('cancel')}
      onPressOverlay={genOnPressBtn('overlay')}
      cancelButtonLoading={state.cancel}
      confirmButtonLoading={state.confirm}
      onPressClose={onPressClose}
    />
  );
};

export default memo(DialogMethod);

import isNil from 'lodash/isNil';
import React, { useRef, useCallback, memo, isValidElement } from 'react';
import { View, Text, Animated } from 'react-native';

import Button from '../button';
import Popup from '../popup/popup';

import type { DialogProps } from './interface';
import { easing, getDefaultValue, renderTextLikeJSX } from '../../helpers';
import { usePersistFn } from '../../hooks';
import { cn } from '../../lib/utils';
import { CrossOutline } from '../icons';

const defaultOnRequestClose = () => {
  return true;
};

/**
 * Dialog å¼¹å‡ºæ¡†
 * @description å¼¹å‡ºæ¨¡æ€æ¡†ï¼Œå¸¸ç”¨äºæ¶ˆæ¯æç¤ºã€æ¶ˆæ¯ç¡®è®¤ï¼Œæˆ–åœ¨å½“å‰é¡µé¢å†…å®Œæˆç‰¹å®šçš„äº¤äº’æ“ä½œã€‚
 * @description å¼¹å‡ºæ¡†ç»„ä»¶æ”¯æŒå‡½æ•°è°ƒç”¨å’Œç»„ä»¶è°ƒç”¨ä¸¤ç§æ–¹å¼ã€‚
 */
const Dialog: React.FC<DialogProps> = ({
  children,
  style,
  title,
  message,
  width,
  messageAlign = 'center',
  showConfirmButton = true,
  showCancelButton = false,
  confirmButtonText,
  cancelButtonText,
  confirmTextClassName,
  confirmButtonTextBold = true,
  cancelTextClassName,
  cancelButtonTextBold = false,
  confirmButtonLoading = false,
  cancelButtonLoading = false,
  showClose = false,
  onPressClose,
  buttonReverse = false,
  onPressCancel,
  onPressConfirm,
  duration,
  onOpen: onOpenFn,
  onClose: onCloseFn,
  onRequestClose = defaultOnRequestClose,
  ...resetProps
}) => {
  const fadeAnim = useRef(new Animated.Value(0)).current;
  const fadeInstance = useRef<Animated.CompositeAnimation | null>(null);

  width = getDefaultValue(width, 300);
  duration = getDefaultValue(duration, 300);

  const showDialog = useCallback(
    (show: boolean) => {
      if (fadeInstance.current) {
        fadeInstance.current.stop();
        fadeInstance.current = null;
      }

      fadeInstance.current = Animated.timing(
        fadeAnim, // åŠ¨ç”»ä¸­çš„å˜é‡å€¼
        {
          toValue: show ? 1 : 0,
          duration: duration,
          useNativeDriver: true,
          easing: show ? easing.easeOutCirc : easing.easeInCubic,
        }
      );

      fadeInstance.current.start();
    },
    [duration, fadeAnim]
  );
  const onOpenPersistFn = usePersistFn(() => {
    showDialog(true);
    onOpenFn?.();
  });
  const onClosePersistFn = usePersistFn(() => {
    showDialog(false);
    onCloseFn?.();
  });

  const titleJSX = renderTextLikeJSX(
    title,
    cn('text-center text-4xl px-6 pt-6 pb-4 text-gray-800', {
      'pb-0': isNil(message),
    })
  );
  const messageJSX = !isNil(message) ? (
    isValidElement(message) ? (
      message
    ) : (
      <Text
        className={cn('px-6 text-4xl text-gray-800')}
        style={{
          textAlign: messageAlign,
        }}
      >
        {message}
      </Text>
    )
  ) : null;

  const cancelButtonProps = {
    text: cancelButtonText ?? 'å–æ¶ˆ',
    loading: cancelButtonLoading,
    onPress: onPressCancel,
  };

  const confirmButtonProps = {
    text: confirmButtonText ?? 'ç¡®è®¤',
    loading: confirmButtonLoading,
    onPress: onPressConfirm,
  };

  // TODO ä¼˜åŒ–é€†è½¬æŒ‰é’®å˜é‡å˜æ¢
  const _showCancelButton = buttonReverse ? showConfirmButton : showCancelButton;
  const _showConfirmButton = buttonReverse ? showCancelButton : showConfirmButton;
  const _cancelButtonTextBold = buttonReverse ? confirmButtonTextBold : cancelButtonTextBold;
  const _confirmButtonTextBold = buttonReverse ? cancelButtonTextBold : confirmButtonTextBold;
  const _cancelButtonProps = buttonReverse ? confirmButtonProps : cancelButtonProps;
  const _confirmButtonProps = buttonReverse ? cancelButtonProps : confirmButtonProps;

  console.log('ğŸš€ ~ cn ~ confirmTextClassName:', confirmTextClassName);

  return (
    <Popup
      {...resetProps}
      duration={duration}
      onOpen={onOpenPersistFn}
      onClose={onClosePersistFn}
      onRequestClose={onRequestClose}
    >
      <Animated.View
        className='overflow-hidden bg-white rounded-2xl'
        style={[
          style,
          {
            width,
            transform: [
              {
                scale: fadeAnim.interpolate({
                  inputRange: [0, 0.01, 0.98, 1],
                  outputRange: [0, 0.9, 1.02, 1],
                }),
              },
            ],
          },
        ]}
      >
        {showClose ? (
          <CrossOutline
            className='absolute top-4 right-4'
            onPress={onPressClose}
            color='#11151A'
            size={20}
          />
        ) : null}

        {titleJSX}

        {titleJSX ? (
          messageJSX
        ) : (
          <View className='pt-6 items-center justify-center'>{messageJSX}</View>
        )}

        {children}

        {_showCancelButton || _showConfirmButton ? (
          <View className='flex-row border-t border-gray-200 mt-4'>
            {_showCancelButton ? (
              <Button
                {..._cancelButtonProps}
                type='link'
                size='xl'
                square
                className='flex-1 m-0'
                textClassName={cn(
                  {
                    'font-bold': _cancelButtonTextBold,
                  },
                  cancelTextClassName
                )}
              />
            ) : null}
            {_showConfirmButton ? (
              <Button
                {..._confirmButtonProps}
                type='link'
                size='xl'
                square
                className={cn('flex-1 m-0', {
                  'border-l border-gray-200': _showCancelButton,
                })}
                textClassName={cn(
                  {
                    'font-bold': _confirmButtonTextBold,
                  },
                  confirmTextClassName
                )}
              />
            ) : null}
          </View>
        ) : null}
      </Animated.View>
    </Popup>
  );
};

export default memo(Dialog);
