import { createContext, useContext } from 'react';
import type { DropdownContext } from './interface';

export const DropdownConfig = createContext<DropdownContext>({} as DropdownContext);

export const useDropdownConfig = () => useContext(DropdownConfig);

import React, { memo } from 'react';
import isNil from 'lodash/isNil';
import { Text } from 'react-native';
import type { DropdownBadgeProps } from './interface';
import { cn } from '../../lib/utils';

const DropdownBadge: React.FC<DropdownBadgeProps> = ({ count, ...restProps }) => {
  if (isNil(count)) {
    return null;
  }

  if (count === true) {
    return (
      <Text {...restProps} className={cn('size-[8px] rounded bg-danger-4', restProps.className)} />
    );
  }

  return (
    <Text {...restProps} className={cn('text-danger-4 text-sm', restProps.className)}>
      {count}
    </Text>
  );
};

export default memo(DropdownBadge);

import { memo, ReactNode, useCallback, useEffect, useRef, useState } from 'react';
import { pick } from 'lodash';
import { Text, TouchableOpacity, View } from 'react-native';
import DropdownPopup from './dropdown-popup';
import DropdownTypeItems, { DropdownTypeItemsProps } from './dropdown-type-items';
import { DropdownPopupProps } from './interface';
import { getDefaultValue } from '../../helpers';
import { cn } from '../../lib/utils';
import { useDropdownConfig } from './context';
import Portal from '../portal';

type FilterTypeButtonProps<T> = {
  /**
   * ÊåâÈíÆÊñáÊú¨
   */
  btnText?: string;
  /**
   * Ëá™ÂÆö‰πâÊåâÈíÆIcon
   */
  customIcon?: ReactNode;
  /**
   * Ëá™ÂÆö‰πâÊï¥‰∏™ÊåâÈíÆÂÖÉÁ¥†
   * @param handleShowPanel Êìç‰ΩúÊòæÁ§∫ÂºπÁ™óÈù¢Êùø
   * @returns
   */
  children?: ({
    handleShowPanel,
  }: {
    /**
     * Êìç‰ΩúÊòæÁ§∫ÂºπÁ™óÈù¢Êùø
     */
    handleShowPanel: () => void;
  }) => ReactNode;
} & DropdownTypeItemsProps<T>;

/**
 * Á≠õÈÄâÁ±ªÂûãÊåâÈíÆÔºàÁÇπÂáªÂºπÂá∫Á≠õÈÄâÈù¢ÊùøÔºâ
 * @param {FilterTypeButtonProps<T>}
 * @returns
 */
function FilterTypeButton<T>({
  btnText = 'Á≠õÈÄâ',
  customIcon,
  children,
  ...restConfig
}: FilterTypeButtonProps<T>) {
  const ViewRef = useRef<View>(null);
  const config = useDropdownConfig();

  const handleShowPanel = useCallback(() => {
    config.MenuRef.current?.measure?.((x, y, width, height, pageX, pageY) => {
      showFilterPopupPanel?.({
        targetHeight: height,
        targetPageY: pageY,
        ...restConfig,
      })
        .then((res) => {
          restConfig?.onValueChange?.(res);
        })
        .catch((error) => {
          console.log('ÁªÑ‰ª∂ÔºöshowFilterPopupPanel Âá∫Èîô', error);

          throw new Error('ÁªÑ‰ª∂ÔºöshowFilterPopupPanel Âá∫Èîô');
        });
    });
  }, [config.MenuRef, restConfig]);

  const renderElement = () => {
    if (!children) {
      return (
        <View
          ref={ViewRef}
          collapsable={false}
          className='h-full w-16 flex-row items-center justify-center'
        >
          <TouchableOpacity
            className={cn('flex-row items-center justify-center gap-x-[2px]')}
            onPress={handleShowPanel}
            activeOpacity={0.8}
          >
            <Text className='text-[14px] font-normal text-[#5E5E5E]'>{btnText}</Text>
            {/* {customIcon ?? <Icon name='search-solid' className='fill-[#B8B8B8]' />} */}
          </TouchableOpacity>
        </View>
      );
    }

    return children({ handleShowPanel });
  };

  return renderElement();
}

interface FilterPopupPanelProps<T> extends Omit<DropdownPopupProps, 'visible'> {
  typeItemProps: DropdownTypeItemsProps<T>;
}

/**
 * Á≠õÈÄâÂºπÁ™óÈù¢Êùø
 * @param props
 * @param {DropdownTypeItemsProps<T>} props.typeItemProps Á≠õÈÄâÁ±ªÂûãÈ°πÈÖçÁΩÆ
 * @param {Omit<DropdownPopupProps, 'visible'>} restProps ÂºπÁ™óÈÖçÁΩÆ
 * @returns
 */
function FilterPopupPanel<T>({
  typeItemProps,
  duration,
  zIndex,
  closeOnPressOutside,
  ...rest
}: FilterPopupPanelProps<T>) {
  const [visible, setVisible] = useState<boolean>(false);

  const config = useDropdownConfig();

  duration = getDefaultValue(duration, config.duration);
  zIndex = getDefaultValue(zIndex, config.zIndex);
  closeOnPressOutside = getDefaultValue(closeOnPressOutside, config.closeOnPressOutside);

  useEffect(() => {
    setVisible(true);
  }, []);

  return (
    <DropdownPopup
      {...rest}
      duration={duration}
      zIndex={zIndex}
      closeOnPressOutside={closeOnPressOutside}
      visible={visible}
      onPressShade={() => setVisible(false)}
      onPressOverlay={() => setVisible(false)}
    >
      <DropdownTypeItems
        {...typeItemProps}
        onValueChange={(res) => {
          typeItemProps.onValueChange?.(res);
          setVisible(false);
        }}
      />
    </DropdownPopup>
  );
}

/**
 * ÊòæÁ§∫Á≠õÈÄâÂºπÁ™óÈù¢Êùø
 * @param options
 * @returns
 */
export function showFilterPopupPanel<T = any>(
  options: { targetHeight: number; targetPageY: number } & DropdownTypeItemsProps<T> &
    Omit<FilterPopupPanelProps<T>, 'typeItemProps'>
) {
  return new Promise<{ action: 'confirm' | 'cancel'; result: Record<string, T | T[]> }>(
    (resolve) => {
      const key = Portal.add(
        <FilterPopupPanel
          {...options}
          contentClassName={cn('py-[20px] px-4', options.contentClassName)}
          typeItemProps={{
            ...pick(options, ['selectedData', 'options', 'confirmText', 'cancelText']),
            onValueChange: (res) => {
              options.onValueChange?.(res);
              resolve(res);
            },
          }}
          onClose={() => {
            Portal.remove(key);
          }}
        />
      );
    }
  );
}

export default memo(FilterTypeButton);

import React, { memo, useMemo } from 'react';
import isNil from 'lodash/isNil';
import { useDropdownConfig } from './context';
import DropdownSelector from './dropdown-selector';
import DropdownText from './dropdown-text';
import type { DropdownItemOption, DropdownItemProps } from './interface';
import { getDefaultValue } from '../../helpers';
import { useControllableValue, usePersistFn } from '../../hooks';
import useState from '../../hooks/useStateUpdate';

const DropdownItem = <T,>({
  popupTestID,
  titleClassName,
  titleTextClassName,
  options,
  duration,
  zIndex,
  closeOnPressOutside,
  loading,
  placeholder = '',

  search,
  onSearch,
  cancellable,

  ...restProps
}: DropdownItemProps<T>) => {
  const config = useDropdownConfig();
  const [active, setActive] = useState(false);
  const [value, onChange] = useControllableValue<T>(restProps);
  const _selectOption = useMemo(() => {
    if (loading) {
      const x: DropdownItemOption<T> = {
        label: 'Âä†ËΩΩ‰∏≠...',
        value: null as any,
      };

      return x;
    }

    let selectOption = {} as DropdownItemOption<T>;

    const findX = (list: DropdownItemOption<T>[]) => {
      list.forEach((item) => {
        if (item.value === value) {
          selectOption = item;
        } else if (item.children?.length) {
          findX(item.children);
        }
      });
    };

    findX(options);

    return selectOption;
  }, [loading, options, value]);

  duration = getDefaultValue(duration, config.duration);
  zIndex = getDefaultValue(zIndex, config.zIndex);
  closeOnPressOutside = getDefaultValue(closeOnPressOutside, config.closeOnPressOutside);

  const onPressText = usePersistFn(() => {
    // ËÆ°ÁÆó Menu ÁöÑ Top ÂíåÂÖÉÁ¥†È´òÂ∫¶

    config.MenuRef.current?.measure((x, y, width, height, pageX, pageY) => {
      setActive(true);
      DropdownSelector({
        targetHeight: height,
        targetPageY: pageY,
        defaultValue: value,
        options,
        duration,
        zIndex,
        closeOnPressOutside,
        activeColor: config.activeColor,
        search,
        onSearch,
        cancellable,
        testID: popupTestID,
      })
        .then((d) => {
          onChange(d.value as T, d.data[0]);
        })
        .catch(() => {})
        .finally(() => {
          setActive(false);
        });
    });
  });

  return (
    <DropdownText
      {...restProps}
      className={titleClassName}
      textClassName={titleTextClassName}
      title={!isNil(_selectOption.label) ? _selectOption.label : placeholder}
      badge={_selectOption.badge}
      active={active}
      onPress={onPressText}
      disabled={restProps.disabled || loading}
    />
  );
};

export default memo(DropdownItem) as <T>(p: DropdownItemProps<T>) => React.ReactElement;

import React, { memo, useEffect, useMemo, useRef } from 'react';
import { BackHandler, Platform, View } from 'react-native';
import { DropdownConfig } from './context';
import type { DropdownMenuProps } from './interface';
import { cn } from '../../lib/utils';

/**
 * DropdownMenu ‰∏ãÊãâËèúÂçïÁöÑÊ®™Êù°
 */
const DropdownMenu: React.FC<DropdownMenuProps> = ({
  titleClassName,
  titleTextClassName,
  iconClassName,
  activeColor,
  direction = 'down',
  duration,
  zIndex = 10,
  closeOnPressOutside = true,
  divider = true,
  ...restProps
}) => {
  const MenuRef = useRef<View>(null);

  const config = useMemo(
    () => ({
      titleClassName,
      titleTextClassName,
      iconClassName,
      activeColor,
      direction,
      duration,
      zIndex,
      closeOnPressOutside,
      MenuRef,
    }),
    [
      activeColor,
      closeOnPressOutside,
      direction,
      duration,
      iconClassName,
      titleClassName,
      titleTextClassName,
      zIndex,
    ]
  );

  useEffect(() => {
    if (Platform.OS === 'ios') {
      return;
    }

    const listener = BackHandler.addEventListener('hardwareBackPress', () => {
      return true;
    });

    return () => {
      listener.remove();
    };
  }, []);

  return (
    <DropdownConfig.Provider value={config}>
      <View
        {...restProps}
        collapsable={false}
        ref={MenuRef}
        className={cn('flex-row items-center h-[40px] bg-white', restProps.className, {
          'border-b border-b-[#F5F5F5]': divider,
        })}
      />
    </DropdownConfig.Provider>
  );
};

export default memo(DropdownMenu);

import React, { memo, useMemo } from 'react';
import isNil from 'lodash/isNil';
import { useDropdownConfig } from './context';
import DropdownSelector from './dropdown-selector';
import DropdownText from './dropdown-text';
import type { DropdownItemOption, DropdownMultipleProps } from './interface';
import { getDefaultValue } from '../../helpers';
import { useControllableValue, usePersistFn } from '../../hooks';
import useState from '../../hooks/useStateUpdate';

const DropdownMultiple = <T,>({
  popupTestID,
  titleClassName,
  titleTextClassName,
  options,
  duration,
  zIndex,
  closeOnPressOutside,
  loading,
  placeholder = '',
  beforeChecked,
  defaultExpandAll,
  search,
  onSearch,
  cancellable,
  multipleMode,
  ...restProps
}: DropdownMultipleProps<T>) => {
  const config = useDropdownConfig();
  const [active, setActive] = useState(false);
  const [value, onChange] = useControllableValue<T[]>(restProps);
  const _selectOptionLabel = useMemo(() => {
    if (loading) {
      return 'Âä†ËΩΩ‰∏≠...';
    }

    const _label: string[] = [];

    const findX = (list: DropdownItemOption<T>[]) => {
      list.forEach((item) => {
        if (value?.indexOf(item.value) > -1) {
          _label.push(item.label);
        }

        if (item.children?.length) {
          findX(item.children);
        }
      });
    };

    findX(options);

    return _label.join('„ÄÅ');
  }, [loading, options, value]);

  duration = getDefaultValue(duration, config.duration);
  zIndex = getDefaultValue(zIndex, config.zIndex);
  closeOnPressOutside = getDefaultValue(closeOnPressOutside, config.closeOnPressOutside);

  const onPressText = usePersistFn(() => {
    // ËÆ°ÁÆó Menu ÁöÑ Top ÂíåÂÖÉÁ¥†È´òÂ∫¶

    config.MenuRef.current?.measure((x, y, width, height, pageX, pageY) => {
      setActive(true);
      DropdownSelector({
        targetHeight: height,
        targetPageY: pageY,
        defaultValue: !isNil(value) ? value : [],
        options,
        duration,
        zIndex,
        closeOnPressOutside,
        activeColor: config.activeColor,
        search,
        onSearch,
        cancellable,
        multiple: true,
        multipleMode,
        testID: popupTestID,
        beforeChecked,
        defaultExpandAll,
      })
        .then((d) => {
          onChange(d.value as T[], d.data);
        })
        .catch(() => {})
        .finally(() => {
          setActive(false);
        });
    });
  });

  return (
    <DropdownText
      {...restProps}
      className={titleClassName}
      textClassName={titleTextClassName}
      title={!isNil(_selectOptionLabel) && !!_selectOptionLabel ? _selectOptionLabel : placeholder}
      active={active}
      onPress={onPressText}
      disabled={restProps.disabled || loading}
    />
  );
};

export default memo(DropdownMultiple) as <T>(p: DropdownMultipleProps<T>) => React.ReactElement;

import React, { useCallback, useMemo, memo } from 'react';
import type { ViewStyle, LayoutChangeEvent } from 'react-native';
import { TouchableWithoutFeedback, View, useWindowDimensions } from 'react-native';
import { useSafeAreaInsets } from 'react-native-safe-area-context';

import useState from '../../hooks/useStateUpdate';
import Popup from '../popup/popup';

import type { DropdownPopupProps } from './interface';
import { cn } from '../../lib/utils';

const POPUP_STYLE: ViewStyle = { backgroundColor: 'transparent' };

const DropdownPopup: React.FC<DropdownPopupProps> = ({
  zIndex,
  closeOnPressOutside = true,
  targetHeight,
  targetPageY,
  onPressShade,
  safeAreaInset = true,
  showShade = true,
  contentClassName,

  children,
  ...restProps
}) => {
  const insets = useSafeAreaInsets();
  const { height: windowHeight } = useWindowDimensions();

  const [wrapperStyle, setWrapperStyle] = useState<ViewStyle>({
    maxHeight: 0,
  });

  /** ÂºπÂá∫Â±ÇÂèØ‰ª•ÁöÑÊúÄÂ§ßÈ´òÂ∫¶ */
  const onLayoutPlace = useCallback((e: LayoutChangeEvent) => {
    setWrapperStyle({
      maxHeight: e.nativeEvent.layout.height,
    });
  }, []);

  const [_isBottom, _shadeStyles, _boxStyles] = useMemo(() => {
    const topHeight = targetPageY - insets.top;
    const bottomHeight = windowHeight - targetPageY - targetHeight - insets.bottom;
    const isBottom = topHeight >= bottomHeight;

    const shadeStyles: ViewStyle = {
      position: 'absolute',
      left: 0,
      right: 0,
      zIndex,
      ...(isBottom
        ? { top: targetPageY, bottom: 0 }
        : { top: 0, height: targetPageY + targetHeight }),
    };

    const boxStyles: ViewStyle = {
      position: 'absolute',
      left: 0,
      right: 0,
      zIndex,
      overflow: 'hidden',
      ...(isBottom
        ? { top: 0, height: targetPageY }
        : { top: targetPageY + targetHeight, bottom: 0 }),
    };

    return [isBottom, shadeStyles, boxStyles];
  }, [insets.bottom, insets.top, targetHeight, targetPageY, windowHeight, zIndex]);

  const placeholderHeight = _isBottom ? insets.top : insets.bottom;
  const placeholderJSX = (
    <TouchableWithoutFeedback onPress={closeOnPressOutside ? onPressShade : undefined}>
      <View style={{ height: placeholderHeight }} />
    </TouchableWithoutFeedback>
  );

  return (
    <>
      {restProps.visible && showShade ? (
        <TouchableWithoutFeedback onPress={closeOnPressOutside ? onPressShade : undefined}>
          <View style={_shadeStyles} />
        </TouchableWithoutFeedback>
      ) : null}

      <View style={_boxStyles} pointerEvents='box-none' onLayout={onLayoutPlace}>
        <Popup {...restProps} style={POPUP_STYLE} position={_isBottom ? 'bottom' : 'top'}>
          <View style={wrapperStyle}>
            {_isBottom && safeAreaInset ? placeholderJSX : null}
            <View
              className={cn('bg-white', contentClassName)}
              style={[
                {
                  maxHeight: (wrapperStyle.maxHeight as number) - placeholderHeight,
                },
              ]}
            >
              {children}
            </View>
            {!_isBottom && safeAreaInset ? placeholderJSX : null}
          </View>
        </Popup>
      </View>
    </>
  );
};

export default memo(DropdownPopup);

import React, { memo, useCallback, useEffect, useMemo, useState } from 'react';
import isNil from 'lodash/isNil';
import uniqBy from 'lodash/uniqBy';
import { Keyboard, Text, TouchableOpacity, View } from 'react-native';
import DropdownBadge from './dropdown-badge';
import DropdownPopup from './dropdown-popup';
import type { DropdownItemOption, DropdownSelectorMethodProps } from './interface';
import { isPromise } from '../../helpers';
import { usePersistFn } from '../../hooks';
import Portal from '../portal';
import Tree from '../tree';
import type { TreeOption, TreeValue } from '../tree/interface';
import { cn } from '../../lib/utils';

const DropdownSelectorMethod = <T,>({
  targetHeight,
  targetPageY,
  onConfirm,
  onCancel,
  defaultValue,
  options,
  duration,
  zIndex,
  closeOnPressOutside,
  onClosed,
  beforeChecked,
  search,
  onSearch,
  cancellable,
  multiple,
  multipleMode,
  defaultExpandAll,
  testID,
}: DropdownSelectorMethodProps<T>) => {
  const [multipleValue, setMultipleValue] = useState<T[]>(
    multiple ? (defaultValue as T[]) || [] : []
  );
  const allOptions = useMemo(() => {
    const findNode = (op: DropdownItemOption<T>[]) => {
      const ooo: DropdownItemOption<T>[] = [];

      op.forEach((o) => {
        ooo.push(o);

        if (o.children?.length) {
          ooo.push(...findNode(o.children));
        }
      });

      return ooo;
    };

    return findNode(options);
  }, [options]);

  const [visible, setVisible] = useState(false);
  const treeOptions = useMemo(() => {
    const convertOption = (ops: DropdownItemOption<T>[]) => {
      const nodes: TreeOption[] = [];

      ops.forEach((item) => {
        const _opt: TreeOption = {
          label: item.label,
          value: item.value as unknown as number | string,
          children: item.children?.length ? convertOption(item.children) : [],
          render: isNil(item.badge)
            ? undefined
            : (p) => {
                return (
                  <View className='flex-1 flex-row items-center'>
                    <Text
                      className={cn('text-[15px] text-[#11151A]', {
                        'text-primary': p.labelHighlight,
                      })}
                      numberOfLines={1}
                    >
                      {p.label}
                    </Text>
                    <DropdownBadge count={item.badge} />
                  </View>
                );
              },
        };

        nodes.push(_opt);
      });

      return nodes;
    };

    return convertOption(options);
  }, [options]);

  useEffect(() => {
    setVisible(true);
  }, []);

  const findNodeByValue = (
    tree: DropdownItemOption<T>[],
    value: T
  ): DropdownItemOption<T> | undefined => {
    for (const item of tree) {
      if (item.value === value) {
        return item;
      }

      if (item.children) {
        const _v = findNodeByValue(item.children, value);

        if (_v) {
          return _v;
        }
      }
    }

    return undefined;
  };

  const onPressShade = useCallback(() => {
    setVisible(false);
    Keyboard.dismiss();
    onCancel?.();
  }, [onCancel]);

  const onRequestClose = usePersistFn(() => {
    onPressShade();

    return true;
  });

  const onChangePersistFn = usePersistFn(
    (
      v: TreeValue | TreeValue[],
      _: TreeOption[],
      event: {
        checked: boolean;
        option: TreeOption;
      }
    ) => {
      if (multiple) {
        if (beforeChecked) {
          const returnVal = beforeChecked({
            value: [],
            checked: event.checked,
            option: event.option,
          });

          if (isPromise(returnVal)) {
            returnVal.then((nv) => {
              setMultipleValue((mv) =>
                uniqBy(
                  [...mv.filter((mvi) => mvi !== event.option.value), ...nv],

                  (x) => x
                )
              );
            });
          } else {
            setMultipleValue((mv) =>
              uniqBy([...mv.filter((mvi) => mvi !== event.option.value), ...returnVal], (x) => x)
            );
          }
        } else {
          setMultipleValue(v as T[]);
        }
      } else {
        setVisible(false);
        Keyboard.dismiss();

        const _v = v as unknown as T;
        const _o = findNodeByValue(options, _v);

        onConfirm?.(_v as unknown as T, _o ? [_o] : []);
      }
    }
  );

  const onConfirmMultiple = usePersistFn(() => {
    setVisible(false);
    Keyboard.dismiss();

    onConfirm?.(
      multipleValue,
      multipleValue.map((item) => {
        return findNodeByValue(options, item)!;
      })
    );
  });

  return (
    <DropdownPopup
      testID={testID}
      targetHeight={targetHeight}
      targetPageY={targetPageY}
      closeOnPressOutside={closeOnPressOutside}
      duration={duration}
      zIndex={zIndex}
      onPressShade={onPressShade}
      visible={visible}
      onRequestClose={onRequestClose}
      onClosed={onClosed}
      onPressOverlay={onPressShade}
    >
      <Tree
        defaultExpandAll={defaultExpandAll}
        minHeight={false}
        search={search}
        options={treeOptions}
        onChange={onChangePersistFn}
        onSearch={onSearch}
        cancellable={cancellable}
        multiple={multiple}
        multipleMode={multipleMode}
        {...(multiple
          ? {
              value: multipleValue as string[],
            }
          : {
              defaultValue: defaultValue as unknown as string,
            })}
      />
      {multiple ? (
        <View className='h-12.5 flex-row items-center overflow-hidden border-t border-t-[#eff3f9] px-3'>
          <View className='relative min-h-0 min-w-0 flex-row items-center pr-2'>
            <TouchableOpacity
              activeOpacity={0.8}
              className='h-11 min-w-23 items-center justify-center px-2'
              onPress={() => {
                if (allOptions.length !== multipleValue.length) {
                  setMultipleValue(allOptions.map((i) => i.value));
                } else {
                  setMultipleValue([]);
                }
              }}
            >
              <Text className='text-center text-base text-primary'>
                {allOptions.length !== multipleValue.length ? 'ÂÖ®ÈÄâ' : 'ÂÖ®‰∏çÈÄâ'}
              </Text>
            </TouchableOpacity>
            <TouchableOpacity
              className='h-11 min-w-23  items-center justify-center rounded bg-primary/15'
              activeOpacity={0.8}
              onPress={onPressShade}
            >
              <Text className='text-center text-base text-primary'>ÂèñÊ∂à</Text>
            </TouchableOpacity>
          </View>
          <View className='flex-1'>
            <TouchableOpacity
              className='h-11 items-center justify-center rounded bg-primary'
              activeOpacity={0.8}
              onPress={onConfirmMultiple}
            >
              <Text className='text-center text-base text-white'>Á°ÆËÆ§</Text>
            </TouchableOpacity>
          </View>
        </View>
      ) : null}
    </DropdownPopup>
  );
};

const DropdownSelectorMethodMemo = memo(DropdownSelectorMethod) as <T>(
  p: DropdownSelectorMethodProps<T>
) => React.ReactElement;

export default <T,>(opt: Omit<DropdownSelectorMethodProps<T>, 'onClosed'>) => {
  return new Promise<{ value: T | T[]; data: DropdownItemOption<T>[] }>((resolve, reject) => {
    const key = Portal.add(
      <DropdownSelectorMethodMemo<T>
        {...opt}
        onCancel={() => {
          opt.onCancel?.();
          reject(new Error());
        }}
        onConfirm={(v, d) => {
          opt.onConfirm?.(v, d);
          resolve({
            value: v,
            data: d,
          });
        }}
        onClosed={() => {
          Portal.remove(key);
        }}
      />
    );
  });
};

