import { attachPropertiesToComponent } from '../../helpers'
import {
  Instance,
  Component,
  StepSelectorComponent,
} from './step-selector-instance'

export default attachPropertiesToComponent(Instance, {
  Component,
  StepSelectorComponent,
})

import React, { memo } from 'react';
import { View } from 'react-native';

import type { StepSelectorLineProps } from './interface';
import { cn } from '../../lib/utils';

const StepSelectorLine: React.FC<StepSelectorLineProps> = ({ index, total, active = false }) => {
  return (
    <View className='w-5 h-full'>
      {index !== 0 ? (
        <View className='w-[1px] h-1/2 -ml-[0.5px] absolute left-1/2 bg-primary-5 top-0' />
      ) : null}
      <View
        className={cn(
          'w-[10px] h-[10px] rounded-[5px] border-primary-5 border-hairline absolute left-1/2 top-1/2 -ml-[5px] -mt-[5px] bg-white',
          {
            'bg-primary-5 border-0': active,
          }
        )}
      />
      {index !== total - 1 ? (
        <View className='w-[1px] h-1/2 -ml-[0.5px] absolute left-1/2 bg-primary-5 bottom-0' />
      ) : null}
    </View>
  );
};

export default memo(StepSelectorLine);

import Portal from '../portal';

import type { StepSelectorMethodProps, StepSelectorProps } from './interface';
import StepSelector from './step-selector';
import StopSelectorMethod from './step-selector-method';

export const Instance = <T,>(opts: StepSelectorMethodProps<T>) => {
  return new Promise<T[]>((resolve, reject) => {
    const key = Portal.add(
      <StopSelectorMethod<T>
        {...opts}
        onClosed={() => {
          Portal.remove(key);
          opts.onClosed?.();
        }}
        onConfirm={(v, o, isEnd) => {
          opts.onConfirm?.(v, o, isEnd);
          resolve(v);
        }}
        onCancel={() => {
          opts.onCancel?.();
          reject(new Error());
        }}
      />
    );
  });
};

export const Component = <T = number,>(props: StepSelectorProps<T>) => {
  return (
    <Portal>
      <StepSelector<T> {...props} />
    </Portal>
  );
};

export const StepSelectorComponent = StepSelector;

import React, { useState, useEffect, memo, useCallback } from 'react';

import type { StepSelectorMethodProps, OptionData } from './interface';
import StopSelector from './step-selector';
import { callInterceptor } from '../../helpers';

function StopSelectorMethod<T>({
  beforeClose,
  onConfirm,
  onCancel,
  ...restProps
}: StepSelectorMethodProps<T>) {
  const [visible, setVisible] = useState(false);

  useEffect(() => {
    setVisible(true);
  }, []);

  const onPressClose = useCallback(() => {
    onCancel?.();
    setVisible(false);
  }, [onCancel]);

  const onRequestClose = useCallback(() => {
    onPressClose();
    return true;
  }, [onPressClose]);

  const onChange = useCallback(
    (v: T[], o: OptionData<T>[], isEnd?: boolean) => {
      if (isEnd) {
        callInterceptor(beforeClose, {
          args: [v, o, isEnd],
          done: () => {
            onConfirm?.(v, o, isEnd);
            setVisible(false);
          },
        });
      }
    },
    [beforeClose, onConfirm]
  );

  return (
    <StopSelector<T>
      {...restProps}
      visible={visible}
      onPressClose={onPressClose}
      onChange={onChange}
      onRequestClose={onRequestClose}
    />
  );
}

export default memo(StopSelectorMethod) as <T>(p: StepSelectorMethodProps<T>) => React.ReactElement;

import groupBy from 'lodash/groupBy';
import isNil from 'lodash/isNil';
import omit from 'lodash/omit';
import React, { useMemo, useEffect, memo, useRef, useCallback } from 'react';
import { View, Text, ScrollView } from 'react-native';

import Cell from '../cell/cell';
import { useControllableValue, usePersistFn, useSafeHeight } from '../../hooks';
import useState from '../../hooks/useStateUpdate';
import Loading from '../loading';
import Popup from '../popup/popup';
import PopupHeader from '../popup/popup-header';

import type { StepSelectorProps, OptionData, RequestResponseData } from './interface';
import StepSelectorLine from './line';
import { SuccessOutline } from '../icons';
import { cn } from '../../lib/utils';

type LocalState<T> = {
  index: number;
  selected: OptionData<T>[];
  loading: boolean;
  responseData: RequestResponseData<T>[];
};

const defaultLoading = <Loading vertical />;

function StepSelector<T = number>({
  title,
  safeAreaInsetTop,
  round = true,
  onPressClose,
  request,
  loading = defaultLoading,

  ...resetProps
}: StepSelectorProps<T>) {
  const safeHeight = useSafeHeight({ top: safeAreaInsetTop });

  const requestPersistFn = usePersistFn(request);
  const ScrollViewRef = useRef<ScrollView>(null);
  const [value, onChange] = useControllableValue<T[]>(resetProps, {
    defaultValue: [],
  });
  const [state, setState] = useState<LocalState<T>>({
    index: 0,
    selected: [],
    loading: false,
    responseData: [],
  });

  const responseDataRef = useRef<Record<string, RequestResponseData<T>>>({});
  const onPressRef = useRef(false);

  const fetchOption = useCallback(
    async (parentId: T, index: number): Promise<RequestResponseData<T>> => {
      const c = responseDataRef.current[`${parentId}`];

      if (c) {
        return c;
      }

      // 请求
      const data = await requestPersistFn(parentId, index);

      responseDataRef.current[`${parentId}`] = data;

      return data;
    },
    [requestPersistFn]
  );

  const optionScrollToTop = useCallback(() => {
    ScrollViewRef.current?.scrollTo({
      x: 0,
      y: 0,
      animated: false,
    });
  }, []);

  useEffect(() => {
    // 构建已选的数据
    if (resetProps.visible) {
      setState({
        loading: true,
      });

      const _value = [...value, null];

      Promise.all(
        _value.map((_, index) => {
          return fetchOption(value[index - 1], index);
        })
      ).then((datas) => {
        const isEnd = !datas[datas.length - 1].options.length;
        const __value = isEnd ? [...value] : _value;
        const selected = __value
          .map((v, index) => {
            const opts = datas[index].options;
            // eslint-disable-next-line max-nested-callbacks
            const vIndex = opts.findIndex((op) => op.value === v);
            return opts[vIndex];
          })
          .filter((v) => !isNil(v));

        setState({
          loading: false,
          index: __value.length - 1,
          responseData: datas,
          selected: selected,
        });

        if (datas[datas.length - 1].options.length) {
          optionScrollToTop();
        }

        if (isEnd && onPressRef.current) {
          onPressRef.current = false;
          onChange(value, selected, true);
        }
      });
    }
  }, [resetProps.visible, fetchOption, value, optionScrollToTop, onChange]);

  const { placeholder, options, groupOption } = useMemo(() => {
    const d = state.responseData[state.index] || {
      options: [],
      placeholder: '',
    };

    const _groupOption = groupBy(d.options, (item) => item.index);

    return {
      placeholder: d.placeholder,
      options: d.options,
      groupOption: Object.keys(_groupOption)
        .sort()
        .map((key) => {
          return _groupOption[key];
        }),
    };
  }, [state.index, state.responseData]);

  return (
    <Popup
      {...omit(resetProps, ['value', 'defaultValue', 'onChange'])}
      position='bottom'
      round={round}
      safeAreaInsetBottom
    >
      <View style={{ height: safeHeight }}>
        <PopupHeader title={title ?? '请选择'} onClose={onPressClose} />

        {state.selected.map((item, index) => {
          return (
            <Cell
              innerClassName='py-0'
              key={`${item?.value}`}
              title={item?.label || state.responseData[index].placeholder}
              titleExtra={
                <StepSelectorLine
                  index={index}
                  total={state.selected.length}
                  active={!!item?.label}
                />
              }
              titleTextClassName={cn('py-3', {
                'text-primary-5': index === state.index && item?.label,
              })}
              isLink
              disabled={state.loading}
              divider={index === state.selected.length - 1}
              onPress={() => {
                setState({
                  index,
                });
                optionScrollToTop();
              }}
            />
          );
        })}

        {options.length && placeholder ? (
          <Cell titleTextClassName='font-bold' title={placeholder} divider={false} />
        ) : null}

        {state.loading && value.length === 0 ? loading : null}

        <ScrollView bounces={false} ref={ScrollViewRef}>
          {groupOption.map((group) => {
            return group.map((item, itemIndex) => {
              const selected = state.selected[state.index];
              const isActive = item.value === selected?.value;

              return (
                <Cell
                  key={`${item.value}`}
                  disabled={state.loading}
                  titleExtra={
                    <Text className='text-gray-600 w-6 text-2xl'>
                      {itemIndex === 0 ? item.index : null}
                    </Text>
                  }
                  title={item.label}
                  titleTextClassName={cn({
                    'text-primary-5': isActive,
                  })}
                  valueExtra={isActive ? <SuccessOutline color='#4080FF' /> : null}
                  divider={false}
                  onPress={() => {
                    // 根据当前的 index 处理数据
                    const sliceEnd = state.index;
                    const newValue = value.slice(0, sliceEnd);

                    const option = newValue.map((v, index) => {
                      const opts = state.responseData[index].options;
                      // eslint-disable-next-line max-nested-callbacks
                      const vIndex = opts.findIndex((op) => op.value === v);
                      return opts[vIndex];
                    });

                    onChange([...newValue, item.value], [...option, item]);

                    onPressRef.current = true;
                  }}
                />
              );
            });
          })}
        </ScrollView>
      </View>
    </Popup>
  );
}

export default memo(StepSelector) as <T = number>(p: StepSelectorProps<T>) => React.ReactElement;
